create table usina(
	id serial primary key,
  	tempo_h numeric,
  	tensao_v numeric,
  	corrente_a numeric,
  	potencia_kw numeric,
  	temperatura_c numeric
);

create table clientes(
  id serial primary key,
  nomecliente text,
  telefone varchar(14),
  cpf varchar(14),
  email text
);

create table usinas(
  id serial primary key,
  nomeusina text,
  endereco text,
  segmento text
 );
  
  
  
create table participacaousinas(
  id serial primary key,
  numerocliente int  references clientes(id),
  usinaid int references usinas(id),
  percentualdeparticipacao int 
);

create table users(
  id serial primary key,
  usuario text not null,
  senha text not null
 );










